def interpreter(s):
   db,res,pos,i=[0],'',0,0
   for i in s:
      if i=='<':
         if pos==0:
            db.insert(0,0)
         else:
            pos-=1
      if i=='>':
         if pos==len(db)-1:
            db.append(0)
         pos+=1
      if i=='+':
         db[pos]+=1
         if db[pos]==256:
            db[pos]=0
      if i=='-':
         db[pos]-=1
         if db[pos]==-1:
            db[pos]=255
      if i=='*':
         res+=chr(db[pos])
      if i=='/':
         db[pos]=0
      if i=='!':
         db.append(0)
   return res

