from itertools import permutations as perm

class Num:
    def __init__(self,n):
        self.n=n

    def __add__(self,other):
        return Num(self.n+other.n)
    
    def __sub__(self,other):
        return Num(self.n-(other.n if isinstance(other,Num) else other))
    
    def __mul__(self,other):
        return Num(self.n*other.n)
    
    def __truediv__(self,other):
        return Num(self.n/other.n) if other.n else Num(0)

    def __lt__(self,other):
        return self.n<other
    
    def __abs__(self):
        return abs(self.n)
    
    def __repr__(self):
        return str(self.n)


