class HexCipher:
    @classmethod
    def encode(cls,s,n):
        while n:
            s=''.join(TEXT2HEX[i] for i in s)
            n-=1
        return s
    
    @classmethod
    def decode(cls,s,n):
        while n:
            s=''.join(TEXT2HEX_rev[s[i]+s[i+1]] for i in range(0,len(s),2))
            n-=1
        return s
    
