def max_match(s):
    r=[]
    while len(s)>1:
        for i in range(len(s),0,-1):
            if s[:i] in VALID_WORDS:
                r.append(s[:i])
                s=s[i:]
                break
        else:
            r.append(s[0])
            s=s[1:]
    return r+([s] if s else [])

